// Save stuff in session data so Piwik javascript [loaded in footer, later in page execution] knows info for search results
// I'm sure there is a better way of doing this in MVC parlance; should improve later once I understand how!
if ( isset($product_total) ) {
	$this->session->data['last_search_total'] = $product_total;
} else {
	unset($this->session->data['last_search_total']);
}

if ( isset($category_id) ) {
	$this->session->data['last_search_category_id'] = $category_id;
} else {
	unset($this->session->data['last_search_category_id']);
}

if ( isset($search) ) {
	$this->session->data['last_search_keyword'] = $search;
} else {
	unset($this->session->data['last_search_keyword']);
